C++<pre><code>// vector_pro::reserve
#include &lt;iostream&gt;
#include "vector_pro.h"

/**
 * Output:
 * making foo grow:
 * capacity changed: 128
 * making bar grow:
 * capacity changed: 100
 */

int main ()
{
  size_type sz;

  vector_pro&lt;int&gt; foo;
  sz = foo.capacity();
  std::cout &lt;&lt; "making foo grow:\n";
  for (int i=0; i&lt;100; ++i) {
    foo.push_back(i);
    if (sz!=foo.capacity()) {
      sz = foo.capacity();
      std::cout &lt;&lt; "capacity changed: " &lt;&lt; sz &lt;&lt; '\n';
    }
  }

  vector_pro&lt;int&gt; bar;
  sz = bar.capacity();
  bar.reserve(100);   // this is the only difference with foo above
  std::cout &lt;&lt; "making bar grow:\n";
  for (int i=0; i&lt;100; ++i) {
    bar.push_back(i);
    if (sz!=bar.capacity()) {
      sz = bar.capacity();
      std::cout &lt;&lt; "capacity changed: " &lt;&lt; sz &lt;&lt; '\n';
    }
  }
  return 0;
}
</code></pre>

